update.foreach:
UPDATE RECIPE
SET	RECIPE_CONTENT = #{list.recipe_content}
WHERE RECIPE_NO = #{list.recipe_no}

mapper.select:
SELECT X.RECIPE_NO, X.RECIPE_NAME, X.RECIPE_CONTENT, X.RECIPE_NUTRIENT, X.RECIPE_CATEGORY, X.RECIPE_REG
FROM(
	SELECT ROWNUM AS RNUM, A.RECIPE_NO, A.RECIPE_NAME, A.RECIPE_CONTENT, A.RECIPE_NUTRIENT, A.RECIPE_CATEGORY, A.RECIPE_REG
	FROM(
		SELECT RECIPE_NO, RECIPE_NAME, RECIPE_CONTENT, RECIPE_NUTRIENT, RECIPE_CATEGORY,RECIPE_REG
		FROM RECIPE
		WHERE RECIPE_CATEGORY = #{category}
		ORDER BY RECIPE_NO DESC
	) A
	WHERE ROWNUM <= #{noOfRecords}
) X
WHERE X.RNUM > #{offset}

SELECT COUNT(*)
FROM RECIPE
WHERE RECIPE_CATEGORY = #{category}
